definition.script:
pipeline {
    agent { node {label "master" }}
    stages {
        stage('retry 2 try/catch') {
            steps {
                retry(2) {
                    script {
                        try {
                            build job: 'COMMON/fail'
                        }
                        catch (e) {
                            build job: 'COMMON/nothing'
                            throw e
                        }
                    }
                }
            }
        }
    }
}
